//leetcode 764. 最大加号标志

#include <cstdio>
#include <vector>
#include <unordered_map>
#include <set>

using namespace std;

int orderOfLargestPlusSign(int N, vector<vector<int>>& mines) {
    //思路：N*N的网格，最大是1+（N-1）/2，这个可以提前剪枝
    //同理，如果出现0，在其4个方向上可以推断除最大K，
    //一种解法就是使用矩阵，然后dfs
    //一种解法就是记录所有可能的阶的点数，比如N为5，则最大的阶为3，只有一个点，

// 00000
// 00100
// 01110
// 00100
// 00000

    vector<vector<int>> matrix(N,vector<int>(N,-1));
    for(int i=0;i<N;++i)
    {
        for(int j=0;j<N;++j)
        {
            if(matrix[i][j] == -1)
            {
                int row_k = min(i+1,N-i);
                int col_k = min(j+1,N-j);
                matrix[i][j] = min(row_k,col_k);
            }
        }
    }

    for(int i=0;i<mines.size();++i)
    {
        int x = mines[i][0];
        int y = mines[i][1];

        matrix[x][y] = 0;
        for(int j=0;j<x;++j)
        {
            int t = min(j+1,x-j);
            matrix[j][y] = min(matrix[j][y],t);
        }

        for(int j=x+1;j<N;++j)
        {
            int t = min(j-x,N-j);
            matrix[j][y] = min(matrix[j][y],t);
        }

        for(int j=0;j<y;++j)
        {
            int t = min(j+1,y-j);
            matrix[x][j] = min(matrix[x][j],t);
        }

        for(int j=y+1;j<N;++j)
        {
            int t = min(j-y,N-j);
            matrix[x][j] =  min(matrix[x][j],t);
        }
    }

    for(int i=0;i<N;++i)
    {
        for(int j=0;j<N;++j)
        {
            if(matrix[i][j] == 239)
            {
                printf("i:%d j:%d\n",i,j);
            }
            // printf("%d ",matrix[i][j]);
        }
        // printf("\n");
    }

    int res = 0;
    for(int i=0;i<N;++i)
    {
        for(int j=0;j<N;++j)
        {
            res = max(res,matrix[i][j]);
        }
    }

    return res;
}

